$(function(){$("a.pagetop").click(function(){$("html,body").animate({scrollTop:0},"slow","swing");return false});$("#notice").removeClass("ui-overlay-shadow");if(/^#(.*)&ui-state=dialog$/.test(location.hash)){var topURL=location.href.split("#")[0];location.replace(topURL)}var $agree=$("#agree");if($agree.size()>0){checkAgree($agree)}$agree.on("change",function(){checkAgree($agree)})});function checkAgree($target){if($target.prop("checked")){$(".login_fb a.current_user, .login_tw a.current_user").removeClass("ui-disabled").off("click")}else{$(".login_fb a.current_user, .login_tw a.current_user").addClass("ui-disabled").on("click",function(){return false})}var $login_link=$(".login_fb .current_user, .login_tw .current_user");$login_link.each(function(){$(this).attr("href",$(this).attr("data-href"))})};